import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { PersonCircle, Lock } from 'react-bootstrap-icons';
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Mock login - in real app would connect to backend
        console.log("Login attempted with:", email);
    };

    return (
        <div className="login-page">
            <Container>
                <Row className="justify-content-center align-items-center" style={{ minHeight: '80vh' }}>
                    <Col md={10} lg={8} xl={6}>
                        <Card className="login-card shadow-lg border-0">
                            <Card.Body className="p-5">
                                <div className="text-center mb-4">
                                    <h2 className="login-title">Welcome Back</h2>
                                    <p className="text-muted">Secure Access for Medical Professionals</p>
                                </div>

                                <Form onSubmit={handleSubmit}>
                                    <Form.Group className="mb-4" controlId="formBasicEmail">
                                        <Form.Label className="login-label">Email Address</Form.Label>
                                        <div className="input-group-custom">
                                            <PersonCircle className="input-icon" />
                                            <Form.Control
                                                type="email"
                                                placeholder="doctor@hospital.com"
                                                className="login-input"
                                                value={email}
                                                onChange={(e) => setEmail(e.target.value)}
                                                required
                                            />
                                        </div>
                                    </Form.Group>

                                    <Form.Group className="mb-4" controlId="formBasicPassword">
                                        <Form.Label className="login-label">Password</Form.Label>
                                        <div className="input-group-custom">
                                            <Lock className="input-icon" />
                                            <Form.Control
                                                type="password"
                                                placeholder="••••••••"
                                                className="login-input"
                                                value={password}
                                                onChange={(e) => setPassword(e.target.value)}
                                                required
                                            />
                                        </div>
                                    </Form.Group>

                                    <div className="d-flex justify-content-between align-items-center mb-4">
                                        <Form.Check type="checkbox" label="Remember me" id="remember-me" className="custom-check" />
                                        <Link to="#" className="forgot-password">Forgot Password?</Link>
                                    </div>

                                    <Button variant="primary" type="submit" className="login-btn w-100 mb-3">
                                        Sign In
                                    </Button>

                                    <div className="text-center mt-4">
                                        <span className="text-muted">Don't have an account? </span>
                                        <Link to="/contact" className="register-link">Request Access</Link>
                                    </div>
                                </Form>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </div>
    );
};

export default Login;
